import React from 'react';
import { Route } from 'react-router-dom';
import { createStructuredSelector } from 'reselect';
import { connect } from 'react-redux';

import CollectionsOverview from '../../components/collection-overview/collection-overview';
import CollectionPage from '../collection/collection';

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';
import { selectIsCollectionFetching, selecIsCollectionsLoaded } from '../../redux/shop/shop.selector';
import WithSpinner from '../../components/spinner/spinner';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

	componentDidMount(){
		const { fetchCollectionsStartAsync} = this.props;
		fetchCollectionsStartAsync();		
	}

	render(){
		const { match, IsCollectionFetching, isCollectionsLoaded } = this.props;
		return (
		  <div className='shop-page'>
		    <Route exact path={`${match.path}`} 
		    render ={props => (<CollectionsOverviewWithSpinner isLoading = {IsCollectionFetching}{...props}/> )}/>
		    <Route path={`${match.path}/:collectionId`} 
		     render = {props => (<CollectionPageWithSpinner isLoading ={!isCollectionsLoaded} {...props}/> )}
		     />
		  </div>
		);
	}
}

const mapStateToProps = createStructuredSelector({
	IsCollectionFetching: selectIsCollectionFetching,
	isCollectionsLoaded: selecIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
	fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(null, mapStateToProps,mapDispatchToProps)(ShopPage);
