import { takeEvery } from 'redux-saga/effects';
import ShopActionTypes  from './shop.type';
import { firestore, convertCollectionsSnapchotToMap } from '../../firebase/firebase.utils';
import { fetchCollectionsSuccess, fetchCollectionsFailure } from './shop.types';


export function* fetchCollectionAsync(){
	yield console.log('I am fired');
	const collectionRef = firestore.collection('collections');

		collectionRef.get().then( snapshot => {
			const collectionsMap = convertCollectionsSnapchotToMap(snapshot);
			dispatch(fetchCollectionsSuccess(collectionsMap));
		}).catch(error=> dispatch(fetchCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart(){
	yield takeEvery(ShopActionTypes.FETCH_COLLECTIONS_START, fetchCollectionAsync);
}
